// src/declarations.d.ts

// Declare for .webp files
declare module '*.webp' {
    const value: string;
    export default value;
  }
  
  // Declare for .png files
  declare module '*.png' {
    const value: string;
    export default value;
  }
  
  // Declare for .jpg and .jpeg files
  declare module '*.jpg' {
    const value: string;
    export default value;
  }
  declare module '*.jpeg' {
    const value: string;
    export default value;
  }
  
  // Declare for .gif files
  declare module '*.gif' {
    const value: string;
    export default value;
  }
  
  // Declare for .svg files
  declare module '*.svg' {
    const value: React.FunctionComponent<React.SVGProps<SVGSVGElement>>;
    export default value;
  }
  
  // Declare for .mp4 files
  declare module '*.mp4' {
    const value: string;
    export default value;
  }
  
  // Declare for .mp3 files
  declare module '*.mp3' {
    const value: string;
    export default value;
  }
  